import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { allMovies } from "../../store/movies";
import MovieCard from "../MovieCard/MovieCard";
import "./search.css"

const Search = () => {
    const dispatch = useDispatch();

    useEffect(() => {

        const handleMovies = (e) => {
            e.preventDefealt();
            dispatch(allMovies())
        }

        const handleTvShows = (e) => {
            e.preventDefealt();
            dispatch(allMovies())
        }


        dispatch(allMovies())
    }, [dispatch])

    const movies = useSelector(state => state.movies)

    return (
        <>
            <div className="initial_search">

                <h1>THE MOVIE CODE</h1>

                <div className="container_form">
                    <form className="form">
                        <input className="input_search" type="text" name="search" placeholder="Search movies or tv shows" />
                        <button type="submit" className="input_sub" >
                            <i className='bx bx-search'></i>
                        </button>
                    </form>
                </div>

                <div className="cat_search">
                    <button onClick={handleMovies} className="btn cat_but1">Movies</button>
                    <button className="btn cat_but2">Tv Shows</button>
                </div>
            </div>

            <ul className="movies_grid">
                {movies.map((movie, i) => (
                    <MovieCard key={i} movie={movie} />
                ))}
            </ul>
        </>
    );
};

export default Search;
