import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { allMovies, searchMovies } from "../../store/movies";
import { allTvShows } from "../../store/tvshows";
import MovieCard from "../MovieCard/MovieCard";
import "./search.css"

const Search = () => {
    const dispatch = useDispatch();
    const [search, setSearch] = useState('')

    const handleMovies = (e) => {
        e.preventDefault();
        dispatch(allMovies())
    }

    const handleTvShows = (e) => {
        e.preventDefault();
        dispatch(allTvShows())
    }

    const handleSearch = (e) => {
        e.preventDefault();
        setSearch(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(searchMovies(search))
    }

    useEffect(() => {
        dispatch(allMovies())
    }, [dispatch])

    const movies = useSelector(state => state.movies)

    return (
        <>
            <div className="initial_search">

                <h1>THE MOVIE CODE</h1>

                <div className="container_form">
                    <form className="form" onSubmit={handleSubmit}>
                        <input onChange={handleSearch} className="input_search" type="text" name="search" placeholder="Search movies or tv shows" />
                        <button type="submit" className="input_sub" >
                            <i className='bx bx-search'></i>
                        </button>
                    </form>
                </div>

                <div className="cat_search">
                    <button onChange={handleMovies} className="btn cat_but1">

                        Movies</button>
                    <button onChange={handleTvShows} className="btn cat_but2">Tv Shows</button>
                </div>
            </div>

            <ul className="movies_grid">
                {movies.map((movie, i) => (
                    <MovieCard key={i} movie={movie} />
                ))}
            </ul>
        </>
    );
};

export default Search;
