import React from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from 'react-redux'
import toast from 'react-hot-toast'
import useInput from "../../hooks/useInput";
import { userRegister } from "../../store/user";
import "./register.css"
import { userValidation } from "../../utils/userValidation";
import * as Yup from 'yup';
import { useFormik } from "formik"


const Register = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const username = useInput();
    const email = useInput();
    const password = useInput();

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(userRegister({
            username: username.value,
            email: email.value,
            password: password.value,
        }))
            .then(() => {
                toast.success('Successfully register!')
                return navigate("/login")
            })
            .catch((error) => console.log(error))
    }


    const { values, handleBlur, handleChange } = useFormik({
        initialValues: {
            username: "",
            email: "",
            password: "",
        },
        validationSchema
    })

    return (
        <>
            <div className="register">

                <div className="background">
                    <div className="shape_reg"></div>
                </div>

                <h3 className="title_register">THE MOVIE CODE</h3>

                <form onSubmit={handleSubmit} className="form_register">

                    <label htmlFor="username">Username</label>
                    <input value={values.username} onChange={handleChange} onBlur={handleBlur} type="text" placeholder="Username" id="username" /* {...username}  */ />

                    <label htmlFor="email">Email</label>
                    <input value={values.email} onChange={handleChange} onBlur={handleBlur} type="email" placeholder="Email" id="email"   /*  {...email} */ />

                    <label htmlFor="password">Password</label>
                    <input value={values.password} onChange={handleChange} onBlur={handleBlur} type="password" placeholder="Password" id="password" /* {...password} */ />

                    <button className="button_register">Register</button>

                    {/* <div className="social">
                        <div className="go">
                            <i className='bx bxl-google'></i>
                            Google
                        </div>
                    </div> */}
                </form>
            </div>
        </>
    )
}
export default Register;
