import React from "react";
import toast from 'react-hot-toast'
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import useInput from "../../hooks/useInput";
import { userLogin } from "../../store/user";
import { loginSchema } from "../../utils/loginSchema";
import "./login.css"

const Login = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();
    const username = useInput();
    const password = useInput();

    const onSubmit = (e) => {
        e.preventDefault();
        dispatch(userLogin({
            username: username.value,
            password: password.value,
        }))
            .then(() => {
                toast.success('Welcome!')
                return navigate("/")
            })
            .catch((error) => console.log(error))
    }

    const { values, errors, touched, isSubmitting, handleBlur, handleChange, handleSubmit } = useFormik({
        initialValues: {
            username: "",
            password: ""
        },
        validationSchema: loginSchema,
        onSubmit
    })

    return (
        <div className="initial_login">

            <div className="background">
                <div className="shape"></div>
            </div>

            <form className="form_login" onSubmit={handleSubmit} >
                <h3 className="title_login">THE MOVIE CODE</h3>

                <label htmlFor="username">Username</label>
                <input
                    value={values.username}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    type="text"
                    placeholder="Username"
                    id="username"
                    className={errors.password && touched.password ? "input-error" : ""}
                    {...username}
                />
                {errors.username && touched.username && <p className="error">{errors.username}</p>}

                <label htmlFor="password">Password</label>
                <input
                    value={values.password}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    type="password"
                    placeholder="Password"
                    id="password"
                    className={errors.password && touched.password ? "input-error" : ""}
                    {...password}
                />
                {errors.password && touched.password && <p 
                className="error">{errors.password}</p>}

                <button disabled={isSubmitting} type="submit" className="button_login">Login</button>

                {/*                 <div className="social">
                    <div className="go">
                        <i className='bx bxl-google'></i>
                        Google
                    </div>
                </div> */}
            </form>

        </div>
    )
}
export default Login;
