import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import MovieCard from "../../commons/MovieCard/MovieCard";
import TvShowCard from "../../commons/TvShowCard/TvShowCard";
import { allMovies } from "../../store/movies";
import { allTvShows } from "../../store/tvshows";
import "./grid.css"

const Grid = () => {
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(allMovies())
        dispatch(allTvShows())
    }, [dispatch])

    const movies = useSelector(state => state.movies)
    const tvshows = useSelector(state => state.tvshows)

    return (
        movies ? (
            <ul className="movies_grid">
                {movies.map((movie, i) => (
                    <MovieCard key={i} movie={movie} />
                ))}
            </ul>
        ) : (
            <ul className="template_grid">
                {tvshows.map((tvshow, i) => (
                    <TvShowCard key={i} tvshow={tvshow} />
                ))}
            </ul>
        )

    )
};

export default Grid;
