import React, { useEffect } from "react";
import "./movie_details.css"
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import placeholder from "../../utils/placeholder.jpg"
import { individualMovie } from "../../store/movies";

const MovieDetails = () => {
    const movies = useSelector(state => state.movies)
    const { id } = useParams();
    const dispatch = useDispatch();

    const year = movies.release_date
    const imageUrl = movies.poster_path ? `https://image.tmdb.org/t/p/original${movies.poster_path}` : placeholder

    useEffect(() => {
        dispatch(individualMovie(id))
    }, [id])

    return (

        <div className="imagen_details" style={{
            backgroundImage: imageUrl
        }}>
            <div className="initial_det">
                <h1>THE MOVIE CODE</h1>
                <div className="bottom-details">
                    <span className="back">
                        <i className='bx bx-chevron-left'></i>
                        <Link to={"/search"} className="link"> BACK </Link>
                    </span>
                </div>

                <div className="info">
                    <div className="favorite">
                        {/* <i className='bx bx-star'></i> */}
                        <i className='bx bxs-star'></i>
                    </div>
                    <div className="movie_title">
                        <h2 className="container_titleD">
                            {movies.original_title}
                        </h2>
                    </div>
                    <div className="movie_year">
                        <h3 className="container_year">{movies.release_date}</h3>
                    </div>
                    <div className="description">
                        <p>
                            {movies.overview}
                        </p>
                    </div>
                    <div className="category">
                        <h3 className="cate1">Movie</h3>
                        <h2 className="cate2">Accion, Suspense</h2>
                    </div>
                </div>
            </div>
        </div>

    )
}

export default MovieDetails;
