import React from "react";
import "./popular_slide.css"
import { useSelector } from "react-redux"
import Slider from 'react-slick';
import { settings } from "../../utils/settings";
import Card from "../Card/Card";


const PopularSlide = () => {

    const movies = useSelector(state => state.movies);
    const tvshows = useSelector(state => state.tvshows);

    let renderMovies,
        renderShows = "";

    renderMovies =
        movies.Response === "True" ? (
            movies.Search.map((movie, index) => (
                <Card key={index} type={movie} />
            ))
        ) : (
            <div className="movies-error">
                <h3>{movies.Error}</h3>
            </div>
        );

    renderShows =
        tvshows.Response === "True" ? (
            tvshows.Search.map((tvshow, index) => (
                <Card key={index} type={tvshow} />
            ))
        ) : (
            <div className="tvshows-error">
                <h3>{tvshows.Error}</h3>
            </div>
        );

    return (
        <div className="movie-wrapper">
            <div className="movie-list">
                <h2 className="slide_title">Movies</h2>
                <div className="movie-container">
                    <Slider {...settings}>
                   {} movies.Search.map((movie, index) => (
                <Card key={index} type={movie} />

                    </Slider>
                </div>
            </div>
            <div className="show-list">
                <h2 className="slide_title">Tv Shows</h2>
                <div className="movie-container">
                    <Slider {...settings}>{renderShows}</Slider>
                </div>
            </div>
        </div>
    )
}

export default PopularSlide;
