import React, { useEffect } from "react";
import "./movie_details.css"
import { Link, useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { individualMovie } from "../../store/movies";
import placeholder from "../../utils/placeholder.jpg"

const MovieDetails = () => {
    const navigate = useNavigate()
    const movie = useSelector(state => state.movies)
    const dispatch = useDispatch();
    const { id } = useParams();

    const imageUrl = movie.poster_path ? `https://image.tmdb.org/t/p/original${movie.poster_path}` : placeholder

    useEffect(() => {
        dispatch(individualMovie(id))
    }, [])


    return (

        <div className="imagen_details" style={{
            backgroundImage: imageUrl
        }}>
            <div className="initial_det">
                <h1>THE MOVIE CODE</h1>
                <div className="bottom-details">
                    <span className="back">
                        <i className='bx bx-chevron-left'></i>
                        <Link to={navigate()} className="link"> BACK </Link>
                    </span>
                </div>

                <div className="info">
                    <div className="favorite">
                        {/* <i className='bx bx-star'></i> */}
                        <i className='bx bxs-star'></i>
                    </div>
                    <div className="movie_title">
                        <h2 className="container_titleD">
                            {movie.original_title}
                        </h2>
                    </div>
                    <div className="movie_year">
                        <h3 className="container_year">{movie.release_date}</h3>
                    </div>
                    <div className="description">
                        <p>
                            {movie.overview}
                        </p>
                    </div>
                    <div className="category">
                        <h3 className="cate1">Movie</h3>
                        <h2 className="cate2">Comedy, Accion</h2>
                    </div>
                </div>
            </div>
        </div>

    )
}

export default MovieDetails;
