import React from "react";
import "./carrousel.css"
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { settings } from "../../utils/settings_movies";

const Carrousel = () => {

    const movies = useSelector((state) => state.movies)

    return (
        <Slider {...settings}>
            <div className="imagen_carrousel" style={{
                backgroundImage: `url("https://image.tmdb.org/t/p/original/zGLHX92Gk96O1DJvLil7ObJTbaL.jpg")`
            }}>
                <div className="initial_carrousel">
                    <h1 className="movie_code">THE MOVIE CODE</h1>
                    <div className="bottom-carrousel">
                        <span className="categories">
                            <Link to={"/search"} className="link_cat">Movies</Link>
                            <Link to={"/search"} className="link_cat">TV Shows</Link>
                        </span>
                    </div>

                    <div className="info">
                        <div className="movie_title">
                            <h2 className="container_title">
                                <Link to={"/movies/id"} className="link_tit">Fantastic Beasts: The Secrets of Dumbledore </Link>
                            </h2>
                        </div>
                        <div className="description">
                            <p className="container_description">
                                Professor Albus Dumbledore knows the powerful, dark wizard Gellert Grindelwald is moving to seize control of the wizarding world. Unable to stop him alone, he entrusts magizoologist Newt Scamander to lead an intrepid team of wizards and witches.
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </Slider>
    );
};

export default Carrousel;
