import React, { useEffect } from "react";
import "./movie_details.css"
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import placeholder from "../../utils/placeholder.png"
import { individualMovie } from "../../store/individual";
import axios from "axios";

const MovieDetails = () => {
    const movie = useSelector(state => state.individual)
    const user = useSelector(state => state.user)
    const { id } = useParams();
    const [existe, SetExiste] = useState(Boolean);
    const dispatch = useDispatch();

    const imageUrl = movie.backdrop_path ? `https://image.tmdb.org/t/p/original${movie.backdrop_path}` : placeholder

    useEffect(() => {
        dispatch(individualMovie(id))
    }, [id])

    const agregarFavoritos = () => {
        const id = movieSelect.id;
        axios.post(`http://www.localhost:3001/api/${user.id}/1/favorite/add`, {
            id: id,
            title: movieSelect.original_title,
            poster_path: movieSelect.poster_path,
            description: movieSelect.overview,
            user: user.id,
            category: categoria,
        });

        SetExiste(true);

        notifyAdd();
    };

    const eliminarFavoritos = () => {
        const id = movieSelect.id;
        const idUser = user.id;

        axios
            .delete(
                `https://moseesee-back.herokuapp.com/api/favourite/${idUser}/${id}/delete`
            )
            .then(() => { })
            .catch((err) => console.log(err));

        SetExiste(false);
        notifyDelete();
    };




    return (

        <div className="imagen_details" style={{
            backgroundImage: `url(${imageUrl})`
        }}>
            <div className="initial_det">
                <h1>THE MOVIE CODE</h1>
                <div className="bottom-details">
                    <span className="back">
                        <i className='bx bx-chevron-left'></i>
                        <Link to={"/search"} className="link"> BACK </Link>
                    </span>
                </div>

                <div className="info">
                    <div className="favorite">
                        {/* <i className='bx bx-star'></i> */}
                        <i className='bx bxs-star'></i>
                    </div>
                    <div className="movie_title">
                        <h2 className="container_titleD">
                            {movie.title}
                        </h2>
                    </div>
                    <div className="movie_year">
                        <h3 className="container_year">{movie.tagline}</h3>
                    </div>
                    <div className="description">
                        <p>
                            {movie.overview}
                        </p>
                    </div>
                    <div className="category">
                        <h3 className="cate1">Movie</h3>
                        <h2 className="cate2">{movie.release_date}</h2>
                    </div>
                </div>
            </div>
        </div>

    )
}

export default MovieDetails;
