import React from "react";
import "./carrousel.css"
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const Carrousel = () => {

    const movies = useSelector(state => state.movies)
    const carrouselMovies = movies.slice(10, 15);

    return (
        <div className="tv_slide">
            <h1 className="movie_code">THE MOVIE CODE</h1>
            <div className="bottom-carrousel">
                <span className="categories">
                    <Link to={"/search/movies"} className="link_cat">Movies</Link>
                    <Link to={"/search/tvshows"} className="link_cat">TV Shows</Link>
                </span>
            </div>
            <Slider {...settings}>
                {carrouselMovies.map((movies, i) =>
                    <div className="item_slidetv" key={i}>
                        <TvShowCard movies={movies} />
                    </div>
                )}
            </Slider>

        </div>
    );
};

export default Carrousel;

