import React, { useEffect } from "react";
import "./popular_slide.css"
import Slider from "react-slick";
import "~slick-carousel/slick/slick.css";
import { popularMovies } from "../../store/movies";
import { useDispatch, useSelector } from "react-redux";

const PopularSlide = () => {
    const settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 500
    };

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(popularMovies)
    }, [dispatch])

    const movies = useSelector(state => state.movies)
    return (
        <div className="body_popular">

            <h2>Center Mode</h2>
            <Slider {...settings}>
                <ul className="template_grid">
                    {movies.map((movie, i) => (
                        <li key={i} movie={movie} />
                    ))}
                </ul>
            </Slider>
        </div>

    )
}

export default PopularSlide;
