import axios from "axios";
import { useState } from "react";
import toast from 'react-hot-toast'

export const agregarFavoritos = (userId, id, title, poster_path, overview, release_date) => {

    const [favorite, SetFavorite] = useState(false);

    if (!userId) throw new Error("You need to be logged in");
    axios.post(`http://www.localhost:3001/api/user/${userId}/favorite/add`, {
        tmdbId: id,
        title: title,
        poster_path: poster_path,
        description: overview,
        release_date: release_date,
    })
        .then(() => "Se agrego tu favorito")
        .catch((error) => console.log(error))

    SetFavorite(true);
    toast.success('Added to Favorites!')
};

export const eliminarFavoritos = ({ userId, id }) => {


    const [favorite, SetFavorite] = useState(false);

    if (!userId) throw new Error("You need to be logged in");
    axios.delete(
        `http://www.localhost:3001/api/user/${userId}/${id}/remove`)
        .then(() => "Eliminado de favoritos")
        .catch(error => console.log(error))

    SetFavorite(false);
    toast.success('Removed to Favorites!')
};
