import { createAsyncThunk, createReducer } from '@reduxjs/toolkit'
import axios from 'axios'

export const allFavorites = createAsyncThunk("ALL_FAVORITES", (username) => {
    return axios.get(`http://localhost:3001/api/user/${username}/favorites`)
        .then(movie => movie.data)
        .catch(error => console.log(error))
});

export const addFavorite = createAsyncThunk("ADD_FAVORITE", (data) => {
    console.log("****DATA ADD FAV***", data)
    return axios.post(`http://www.localhost:3001/api/user/${data.userId}/favorite/add`, {
        tmdbId: data.movie.id,
        title: data.movie.title ? data.movie.title : data.movie.name,
        poster_path: data.movie.poster_path,
        description: data.movie.overview,
        release_date: data.movie.release_date ? data.movie.release_date : data.movie.first_air_date,
        category: data.movie.title ? "movies" : "tvshows",
    })
        .then(movie => movie.data)
        .catch((error) => console.log(error.response))
});

export const removeFavorite = createAsyncThunk("REMOVE_FAVORITE", (userId, movieId) => {
    return axios.delete(
        `http://www.localhost:3001/api/user/${userId}/${movieId}/remove`)
        .then(movie => movie.data)
        .catch(error => console.log(error.response))
});

const favoritesReducer = createReducer([], {
    [allFavorites.fulfilled]: (state, action) => action.payload,
    [addFavorite.fulfilled]: (state, action) => action.payload,
    /*  [removeFavorite.fulfilled]: (state, action) => {
         state.favorites = state.favorites.filter(
             (fav) => fav.id !== action.payload.id);
     }, */
});

const stateFavoriteReducer = createReducer([], {
    [allFavorites.fulfilled]: (state, action) => action.payload,
    [addFavorite.fulfilled]: (state, action) => action.payload,
    /*  [removeFavorite.fulfilled]: (state, action) => {
         state.favorites = state.favorites.filter(
             (fav) => fav.id !== action.payload.id);
     }, */
});

export default favoritesReducer;
