import React from "react";
import "./carousel_card.css"
import placeholder from "../../utils/placeholder.png";
import { Link } from "react-router-dom";


const CarouselCard = ({ movie }) => {

    const imageUrl = movie.backdrop_path ? `https://image.tmdb.org/t/p/w500${movie.backdrop_path}` : placeholder

    return (
        <Link to={`/search/movies/${movie.id}`}>

            <div className="imagen_carrousel" style={{
                backgroundImage: `url("https://image.tmdb.org/t/p/original/xKb6mtdfI5Qsggc44Hr9CCUDvaj.jpg")`
            }}>
                <img
                    className="carousel_card"
                    src={imageUrl}
                    alt={movie.original_name}
                />
            </div>
        </Link>
    )
};

export default CarouselCard;

