import toast from 'react-hot-toast'
import axios from "axios";


const useFavorite = ({ userId, movie }) => {

  const [add, setAdd] = useState(false)

  function agregarFavoritos() {
    if (!userId) throw toast.error('You need to be logged in');
    axios.post(`http://www.localhost:3001/api/user/${userId}/favorite/add`, {
      tmdbId: movie.id,
      title: movie.title,
      poster_path: movie.poster_path,
      description: movie.overview,
      release_date: movie.release_date,
    })
      .then(() => "Se agrego tu favorito")
      .catch((error) => console.log(error))
    setAdd(true)
    toast.success('Added to Favorites!')
  };


  function eliminarFavoritos() {
    if (!userId) throw toast.error('You need to be logged in');
    axios.delete(
      `http://www.localhost:3001/api/user/${userId}/${movie.id}/remove`)
      .then(() => "Eliminado de favoritos")
      .catch(error => console.log(error))
    setAdd(false)

    toast.success('Removed to Favorites!')
  };

  return { agregarFavoritos, eliminarFavoritos }

}

export default useFavorite
