import React from "react";
import { Link, useLocation } from "react-router-dom";
import User from "./User";
import "./user_list.css"
import { useDispatch } from "react-redux";


const UserList = () => {

    const users = useLocation((state) => state.users);
    const user = useLocation((state) => state.user);

    useEffect(() => {
        dispatch(showUsers(user.id))
    }, [dispatch])

    return (
        <div className="initial_users">
            <h1 className="movie_code">THE MOVIE CODE</h1>

            <h2 className="title_users">Users</h2>

            <div className="users_container">

                {users.map((user, i) =>

                (
                    <div key={i}>
                        <Link to="/">
                            <div> {user.username}</div>
                        </Link>
                    </div>
                )

                )}


                <User />
            </div>
        </div>
    )
};

export default UserList;
