import { createAsyncThunk, createReducer } from '@reduxjs/toolkit'
import axios from 'axios'

export const allTvShows = createAsyncThunk("ALL_MOVIES", () => {
    return axios.get("http://localhost:3001/api/movies")
        .then(user => user.data)
        .catch(error => console.log(error))
});



const tvShowsReducer = createReducer([], {
    [allTvShows.fulfilled]: (state, action) => action.payload,
    [individualMovie.fulfilled]: (state, action) => action.payload,
    [searchMovies.fulfilled]: (state, action) => action.payload,

});

export default tvShowsReducer;
