import { createAsyncThunk, createReducer } from '@reduxjs/toolkit'
import axios from 'axios'

export const allFavorites = createAsyncThunk("ALL_FAVORITES", (userId) => {
    return axios.get(`http://localhost:3001/api/user/${userId}/favorites`)
        .then(movie => movie.data)
        .catch(error => console.log(error))
})

export const addToFavorites = createAsyncThunk("ADD_TO_FAVORITES", (thunkAPI, data) => {
    const { user } = thunkAPI.getState();
    if (!user.id) throw new Error("You need to be logged in");
    return axios.post(`http://www.localhost:3001/api/user/${user.id}/favorite/add`, data)
        .then((favorite) => favorite)
        .catch(error => console.log(error))
})

export const removeFromFavorites = createAsyncThunk("REMOVE_FROM_FAVORITES", (thunkAPI, tmdbId) => {
    const { user } = thunkAPI.getState();
    if (!user.id) throw new Error("You need to be logged in");
    return axios.delete(
        `http://www.localhost:3001/api/user/${user.id}/${tmdbId}/remove`
    )
        .then((favorite) => favorite)
        .catch(error => console.log(error))
});

const favoritesReducer = createReducer([], {
    [allFavorites.fulfilled]: (state, action) => action.payload,
    [addToFavorites.fulfilled]: (state, action) => {
        state.favorites.push(action.payload);
    },
    [removeFromFavorites.fulfilled]: (state, action) => {
        state.favorites = state.favorites.filter(
            (fav) => fav.id !== action.payload.id
        );
    },
});

export default favoritesReducer;
