import React, { useEffect } from "react";
import "./popular_slide.css";
import { useDispatch, useSelector } from "react-redux"
import { popularMovies } from "../../store/popular";
import Prueba from "./Prueba"
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { settings } from "../../utils/settings";

const PopularSlide = () => {

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(popularMovies())
    }, [dispatch])

    const popular = useSelector(state => state.popular);

    return (
        <div className="smooth">
            <h2>Popular</h2>
{/*             <div className="wrapper">
                <section id="section1"> */}
                    <Slider {...settings}>
                        {/*               <a href="#section3" className="arrow__btn left-arrow">‹</a> */}
                        {popular.map((movie, i) =>
                            <div className="item">
                                <Prueba key={i} movie={movie} />
                            </div>
                        )}

                        {/*       <a href="#section2" className="arrow__btn right-arrow">›</a> */}
                    </Slider>
                {/* </section>
            </div> */}
        </div>

    )
}

export default PopularSlide;

