import React, { useEffect } from "react";
import { Toaster } from 'react-hot-toast'
import { Route, Routes } from "react-router";
import Sidebar from "./components/Sidebar/Sidebar"
import Footer from "./components/Footer/Footer"
import Home from "./components/Home/Home";
import MovieDetails from "./commons/MovieDetails/MovieDetails";
import Search from "./commons/Search/Search";
import Favorites from "./commons/Favorites/Favorites";
import UserList from "./commons/UsersList/UserList";
import Register from "./commons/Register/Register";
import Login from "./commons/Login/Login";
import TvShowDetails from "./commons/TvShowDetails/TvShowDetails";
import axios from "axios";
import toast from 'react-hot-toast'
import { useDispatch, useSelector } from "react-redux";
import { useStorage } from "./hooks/useStorage";
import { allFavorites } from "./store/favorites";

/* import Grid from "./components/Grid/Grid"; */

const App = () => {

  const user = useStorage();
  const movie = useSelector(state => state.individual)

  const dispatch = useDispatch();

  useEffect(() => {
      dispatch(allFavorites(user.id))
  }, [dispatch])


  const agregarFavoritos = () => {
    if (!user.id) throw toast.error('You need to be logged in');
    axios.post(`http://www.localhost:3001/api/user/${user.id}/favorite/add`, {
        tmdbId: movie.id,
        title: movie.title,
        poster_path: movie.poster_path,
        description: movie.overview,
        release_date: movie.release_date,
    })
        .then(() => "Se agrego tu favorito")
        .catch((error) => console.log(error))

    toast.success('Added to Favorites!')
};

const eliminarFavoritos = () => {
    if (!user.id) throw toast.error('You need to be logged in');
    axios.delete(
        `http://www.localhost:3001/api/user/${user.id}/${movie.id}/remove`)
        .then(() => "Eliminado de favoritos")
        .catch(error => console.log(error))

    toast.success('Removed to Favorites!')
};


  return (
    <div>
      <Sidebar />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/movies/:id" element={<MovieDetails />} />
        <Route path="/tvshows/:id" element={<TvShowDetails />} />
        <Route path="/search" element={<Search />} />
        <Route path="/search?q=:search" element={<Search />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/favorites" element={<Favorites />} />
        <Route path="/users" element={<UserList />} />

        {/* VER COMO LO PUEDO IMPLEMENTAR MÁS ADELANTE */}
        {/* <Route path="/search/movies" element={<Grid />} /> 
        <Route path="/search/tvshows" element={<Grid />} /> */}
      </Routes>

      <Footer />
      <Toaster
        style={{
          borderRadius: '10px',
          background: '#333',
          color: '#fff',
        }
        }
        position="top-right"
        toastOptions={{
          duration: 1500,
          style: {
            borderRadius: '10px',
            background: '#333',
            color: '#fff',
          }
        }} />
    </div>
  );
};

export default App;
