import React from "react";
import toast from 'react-hot-toast'
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { userLogin } from "../../store/user";
import { loginSchema } from "../../utils/loginSchema";
import "./login.css"
import { useFormik } from "formik"

const Login = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const onSubmit = (values) => {

        dispatch(userLogin({
            username: values.username,
            password: values.password,
        }))
            .then((user) => {
                if (Number(user.payload)) {
                    return navigate("/login")
                    toast.success('Welcome!')
                    return navigate("/")
                } else {

                }

            })
            .catch((error) => error)
    }

    const { values, errors, touched, isSubmitting, handleBlur, handleChange, handleSubmit } = useFormik({
        initialValues: {
            username: "",
            password: ""
        },
        validationSchema: loginSchema,
        onSubmit
    })

    return (
        <div className="initial_login">

            <div className="background">
                <div className="shape"></div>
            </div>

            <form className="form_login" onSubmit={handleSubmit} >
                <h3 className="title_login">THE MOVIE CODE</h3>

                <label htmlFor="username">Username</label>
                <input
                    value={values.username}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    type="text"
                    placeholder="Username"
                    id="username"
                    className={errors.password && touched.password ? "input-error" : ""}
                />
                {errors.username && touched.username && <p className="error">{errors.username}</p>}

                <label htmlFor="password">Password</label>
                <input
                    value={values.password}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    type="password"
                    placeholder="Password"
                    id="password"
                    className={errors.password && touched.password ? "input-error" : ""}
                />
                {errors.password && touched.password && <p
                    className="error">{errors.password}</p>}

                <button disabled={isSubmitting} type="submit" className="button_login">Login</button>

                {/*                 <div className="social">
                    <div className="go">
                        <i className='bx bxl-google'></i>
                        Google
                    </div>
                </div> */}
            </form>

        </div>
    )
}
export default Login;
